const Redis = require('ioredis');

// Replace with your Redis Cloud credentials
const redis = new Redis({
  host: 'redis-19666.c16.us-east-1-3.ec2.redns.redis-cloud.com',  // e.g., 'redis-12345.c12.us-east-1-3.ec2.cloud.redislabs.com'
  port: 19666,              // Your Redis port
  password: 'p7LS233UJKNl8F4eCSrb8OSWnluC9MLB'
});

// Test the connection by setting and getting a key
redis.set('key', 'value', (err) => {
  if (err) console.error('Error setting key:', err);
});

redis.get('key', (err, result) => {
  if (err) console.error('Error getting key:', err);
  console.log(result);  // 'value'
});

export {redis};
