import React, { useEffect, useState } from 'react';
import './App.css'; // Importing the CSS file
import axios from 'axios';

const App = () => {
    const [xMin, setXMin] = useState('');
    const [xMax, setXMax] = useState('');
    const [yMin, setYMin] = useState('');
    const [yMax, setYMax] = useState('');
    const [numPoints, setNumPoints] = useState('');
    const [timeMin, setTimeMin] = useState('');
    const [timeMax, setTimeMax] = useState('');
    const [data, setData] = useState([]); 
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(''); 
    const [isLocked, setIsLocked] = useState(false); // Lock state to disable fields and submit

    // Update seconds every second
    useEffect(() => {
        const intervalId = setInterval(() => {
            setData(prevData =>
                prevData
                    .map(item => ({
                        ...item,
                        seconds: item.seconds - 1, // Decrement seconds
                    }))
                    .filter(item => item.seconds > 0) // Keep only items with seconds > 0
                    .sort((a, b) => b.seconds - a.seconds) // Sort in descending order by seconds
            );
        }, 1000); 

        return () => clearInterval(intervalId); // Clear interval on component unmount
    }, []);

    // Lock/unlock fields based on data table status
    useEffect(() => {
        if (data.length === 0) {
            setIsLocked(false); // Unlock when table is empty
        } else {
            setIsLocked(true); // Lock when table has data
        }
    }, [data]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const xMinNum = parseInt(xMin);
        const xMaxNum = parseInt(xMax);
        const yMinNum = parseInt(yMin);
        const yMaxNum = parseInt(yMax);
        const numPointsNum = parseInt(numPoints);
        const timeMinNum = parseInt(timeMin);
        const timeMaxNum = parseInt(timeMax);

        if (!xMin || !xMax || !yMin || !yMax || !numPoints || !timeMin || !timeMax) {
            setError('All fields are required. Please fill in all values.');
            return;
        }

        if (isNaN(xMinNum) || isNaN(xMaxNum) || isNaN(yMinNum) || isNaN(yMaxNum) || 
            isNaN(numPointsNum) || isNaN(timeMinNum) || isNaN(timeMaxNum)) {
            setError('All inputs must be valid numbers.');
            return;
        }

        if (xMinNum < 0 || xMinNum > 90 || 
            xMaxNum < xMinNum + 10 || 
            xMaxNum > 100) {
            setError('Invalid X values. X Min must be between 0 and 90, and X Max must be at least X Min + 10 (up to 100).');
            return;
        }

        if (yMinNum < 0 || yMinNum > 90 || 
            yMaxNum < yMinNum + 10 || 
            yMaxNum > 100) {
            setError('Invalid Y values. Y Min must be between 0 and 90, and Y Max must be at least Y Min + 10 (up to 100).');
            return;
        }

        if (numPointsNum < 2 || numPointsNum > 10) {
            setError('Number of points must be between 2 and 10.');
            return;
        }

        if (timeMinNum < 5 || timeMinNum > 60 || 
            timeMaxNum < timeMinNum || 
            timeMaxNum > 60) {
            setError('Time values must be between 5 and 60 seconds, and Max must be greater than Min.');
            return;
        }

        setError(''); 
        fetchData();
    };

    const fetchData = async () => {
        setLoading(true); 
        try {
            const arr = [
                parseInt(xMin),
                parseInt(xMax),
                parseInt(yMin),
                parseInt(yMax),
                parseInt(numPoints),
                parseInt(timeMin),
                parseInt(timeMax),
            ];

            const response = await axios.post('http://localhost:8080/api/settings', arr);
            console.log('Response from backend:', response.data); 

            setData([]); 
        } catch (error) {
            console.error('Error posting data:', error);
            setError('Error posting data: ' + (error.response?.data || error.message));
        } finally {
            setLoading(false); 
            axios.get('http://localhost:5000/data')
            .then((response) => {
              setData(response.data.data);
            })
            .catch((error) => {
              console.error('Error fetching data:', error);
            });
        }   
    };

    return (
        <div className="app-container">
            <h1>RGraph</h1>

            <div className="horizontal-container">
                {/* Input Area */}
                <div className="input-container">
                    <h2>Input Values</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="input-group">
                            <label>X Range:</label>
                            <input 
                                type="number" 
                                placeholder="MIN" 
                                value={xMin} 
                                onChange={(e) => setXMin(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                            <input 
                                type="number" 
                                placeholder="MAX" 
                                value={xMax} 
                                onChange={(e) => setXMax(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                        </div>
                        <div className="input-group">
                            <label>Y Range:</label>
                            <input 
                                type="number" 
                                placeholder="MIN" 
                                value={yMin} 
                                onChange={(e) => setYMin(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                            <input 
                                type="number" 
                                placeholder="MAX" 
                                value={yMax} 
                                onChange={(e) => setYMax(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                        </div>
                        <div className="input-group">
                            <label>Number of Points:</label>
                            <input 
                                type="number" 
                                value={numPoints} 
                                onChange={(e) => setNumPoints(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                        </div>
                        <div className="input-group">
                            <label>Time Range (seconds):</label>
                            <input 
                                type="number" 
                                placeholder="MIN" 
                                value={timeMin} 
                                onChange={(e) => setTimeMin(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                            <input 
                                type="number" 
                                placeholder="MAX" 
                                value={timeMax} 
                                onChange={(e) => setTimeMax(e.target.value)} 
                                disabled={isLocked} // Disable field if locked
                            />
                        </div>
                        <button type="submit" disabled={isLocked}>Submit</button> {/* Disable submit if locked */}
                        {error && <p className="error-message">{error}</p>} 
                    </form>
                </div>

                {/* Data Table */}
                <div className="data-table-container">
                    <h2>Data Table</h2>
                    {loading ? (
                        <p>Loading data...</p>
                    ) : (
                        <table className="data-table">
                            <thead>
                                <tr>
                                    <th>X</th>
                                    <th>Y</th>
                                    <th>Seconds</th> 
                                </tr>
                            </thead>
                            <tbody>
                                {data.map((item, index) => (
                                    <tr key={index}>
                                        <td>{item.x}</td>
                                        <td>{item.y}</td>
                                        <td>{item.seconds}</td> 
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>

                {/* Chart */}
                <div className="chart-container">
                    <h2>Graph</h2>
                    <p>Your graph will be displayed here.</p>
                </div>
            </div>
        </div>
    );
};

export default App;
