import React, { useState } from 'react';
import './App.css'; // ייבוא קובץ ה-CSS

const App = () => {
    const [xMin, setXMin] = useState('');
    const [xMax, setXMax] = useState('');
    const [yMin, setYMin] = useState('');
    const [yMax, setYMax] = useState('');
    const [numPoints, setNumPoints] = useState('');
    const [timeMin, setTimeMin] = useState('');
    const [timeMax, setTimeMax] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log({ xMin, xMax, yMin, yMax, numPoints, timeMin, timeMax });
    };

    return (
        <div className="app-container">
            <h1>ברוך הבא לאפליקציה</h1>
            <div className="input-container">
                <h2>הזן ערכים</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label>טווח ערכי X:</label>
                        <input type="number" placeholder="MIN" value={xMin} onChange={(e) => setXMin(e.target.value)} />
                        <input type="number" placeholder="MAX" value={xMax} onChange={(e) => setXMax(e.target.value)} />
                    </div>
                    <div className="input-group">
                        <label>טווח ערכי Y:</label>
                        <input type="number" placeholder="MIN" value={yMin} onChange={(e) => setYMin(e.target.value)} />
                        <input type="number" placeholder="MAX" value={yMax} onChange={(e) => setYMax(e.target.value)} />
                    </div>
                    <div className="input-group">
                        <label>מס' נקודות לייצור:</label>
                        <input type="number" value={numPoints} onChange={(e) => setNumPoints(e.target.value)} />
                    </div>
                    <div className="input-group">
                        <label>טווח זמנים (שניות):</label>
                        <input type="number" placeholder="MIN" value={timeMin} onChange={(e) => setTimeMin(e.target.value)} />
                        <input type="number" placeholder="MAX" value={timeMax} onChange={(e) => setTimeMax(e.target.value)} />
                    </div>
                    <button type="submit">שלח</button>
                </form>
            </div>
        </div>
    );
};

export default App;
